import { useState } from 'react';
import { addPaciente } from '../utils/database';

export default function PatientRecordForm() {
  const [form, setForm] = useState({ nombre: '', edad: '', diagnostico: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addPaciente(form);
      alert('Paciente registrado!');
      setForm({ nombre: '', edad: '', diagnostico: '' });
    } catch (error) {
      alert('Error: ' + error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4">
      <input
        value={form.nombre}
        onChange={(e) => setForm({...form, nombre: e.target.value})}
        className="w-full p-2 border rounded"
        placeholder="Nombre"
        required
      />
      <input
        type="number"
        value={form.edad}
        onChange={(e) => setForm({...form, edad: e.target.value})}
        className="w-full p-2 border rounded"
        placeholder="Edad"
        required
      />
      <textarea
        value={form.diagnostico}
        onChange={(e) => setForm({...form, diagnostico: e.target.value})}
        className="w-full p-2 border rounded"
        placeholder="DiagnÃ³stico"
      />
      <button 
        type="submit" 
        className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
      >
        Guardar
      </button>
    </form>
  );
}
