import React, { useState, useEffect } from 'react';
import { initDB, loginUser, registerUser, getCurrentUser, logoutUser } from './utils/database';
import AuthLoginForm from './components/AuthLoginForm';
import AuthRegisterForm from './components/AuthRegisterForm';
import PatientList from './components/PatientList';
import PatientRecordForm from './components/PatientRecordForm';
import AppointmentManagement from './components/AppointmentManagement';
import EducationalMaterials from './components/EducationalMaterials';
import NavigationSidebar from './components/NavigationSidebar';
import DashboardStats from './components/DashboardStats';
import UserManagement from './components/UserManagement';

const App = () => {
  const [currentView, setCurrentView] = useState('login');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [appInitialized, setAppInitialized] = useState(false);
  const [error, setError] = useState(null);

  // Inicialización de la aplicación
  useEffect(() => {
    const initializeApp = async () => {
      try {
        console.log('Inicializando base de datos...');
        await initDB();
        console.log('Base de datos lista. Verificando usuario...');

        const user = await getCurrentUser();
        if (user) {
          setIsAuthenticated(true);
          setUserRole(user.rol);
          setCurrentUser(user);
          setCurrentView('dashboard');
          console.log('Usuario autenticado:', user.email);
        }
      } catch (err) {
        console.error('Error crítico en inicialización:', err);
        setError('Error al conectar con la base de datos. Recarga la página.');
      } finally {
        setAppInitialized(true);
      }
    };

    initializeApp();
  }, []);

  const handleLogin = async (email, password) => {
    try {
      const user = await loginUser(email, password);
      if (user) {
        setIsAuthenticated(true);
        setUserRole(user.rol);
        setCurrentUser(user);
        setCurrentView('dashboard');
        setError(null);
      } else {
        throw new Error('Credenciales incorrectas');
      }
    } catch (err) {
      console.error('Error en login:', err);
      setError(err.message || 'Error al iniciar sesión');
      throw err;
    }
  };

  const handleRegister = async (userData) => {
    try {
      await registerUser(userData);
      alert('¡Registro exitoso! Por favor inicia sesión');
      setShowRegister(false);
      setError(null);
      return true;
    } catch (err) {
      console.error('Error en registro:', err);
      setError(err.message);
      throw err;
    }
  };

  const handleLogout = async () => {
    try {
      await logoutUser();
      setIsAuthenticated(false);
      setUserRole(null);
      setCurrentUser(null);
      setCurrentView('login');
      setShowRegister(false);
      setError(null);
    } catch (err) {
      console.error('Error al cerrar sesión:', err);
      setError('Error al cerrar sesión');
    }
  };

  const renderView = () => {
    switch(currentView) {
      case 'patients':
        return <PatientList mode={userRole === 'cuidador' ? 'view' : 'edit'} />;
      case 'records':
        return <PatientRecordForm />;
      case 'appointments':
        return <AppointmentManagement userRole={userRole} />;
      case 'education':
        return <EducationalMaterials />;
      case 'users':
        return userRole === 'administracion' ? <UserManagement /> : 
          <div className="p-4 text-red-500">Acceso no autorizado</div>;
      case 'dashboard':
      default:
        return <DashboardStats userRole={userRole} />;
    }
  };

  if (!appInitialized) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
          <p className="mt-2">Cargando aplicación...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center p-6 bg-red-50 rounded-lg max-w-md">
          <h2 className="text-xl font-bold text-red-600">Error</h2>
          <p className="mt-2">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Reintentar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {!isAuthenticated ? (
        <div className="flex items-center justify-center min-h-screen p-4">
          {showRegister ? (
            <AuthRegisterForm 
              onRegister={handleRegister} 
              onSwitchToLogin={() => setShowRegister(false)} 
              error={error}
            />
          ) : (
            <AuthLoginForm 
              onLogin={handleLogin} 
              onSwitchToRegister={() => setShowRegister(true)} 
              error={error}
            />
          )}
        </div>
      ) : (
        <div className="flex">
          <NavigationSidebar 
            currentView={currentView}
            setCurrentView={setCurrentView}
            userRole={userRole}
            onLogout={handleLogout}
          />

          <div className="flex-1 p-6">
            <div className="bg-white rounded-lg shadow p-6">
              <div className="mb-4 flex justify-between items-center">
                <h2 className="text-xl font-semibold">
                  {currentView === 'dashboard' && 'Dashboard'}
                  {currentView === 'patients' && 'Pacientes'}
                  {currentView === 'records' && 'Registros'}
                  {currentView === 'appointments' && 'Citas'}
                  {currentView === 'education' && 'Material Educativo'}
                  {currentView === 'users' && 'Usuarios'}
                </h2>
                <span className="text-sm text-gray-500">
                  {currentUser?.nombre} ({currentUser?.rol})
                </span>
              </div>
              {renderView()}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
