import React, { useState, useEffect } from 'react';
import { 
  initDB, 
  loginUser, 
  registerUser, 
  getCurrentUser, 
  logoutUser
} from './utils/database';
import AuthLoginForm from './components/AuthLoginForm';
import AuthRegisterForm from './components/AuthRegisterForm';
import PatientList from './components/PatientList';
import PatientRecordForm from './components/PatientRecordForm';
import AppointmentManagement from './components/AppointmentManagement';
import EducationalMaterials from './components/EducationalMaterials';
import NavigationSidebar from './components/NavigationSidebar';
import DashboardStats from './components/DashboardStats';
import UserManagement from './components/UserManagement';
import { Capacitor } from '@capacitor/core';

const App = () => {
  const [appState, setAppState] = useState({
    currentView: 'login',
    isAuthenticated: false,
    userRole: null,
    showRegister: false,
    currentUser: null,
    appInitialized: false,
    error: null,
    isWeb: !Capacitor.isNativePlatform()
  });

  // Inicialización
  useEffect(() => {
    const initializeApp = async () => {
      try {
        console.log('Iniciando aplicación en modo:', appState.isWeb ? 'Web' : 'Nativo');

        await initDB();
        const user = await getCurrentUser();

        if (user) {
          updateAppState({
            isAuthenticated: true,
            userRole: user.rol,
            currentUser: user,
            currentView: 'dashboard'
          });
        }
      } catch (error) {
        handleAppError(error);

        // Autologin para desarrollo web
        if (appState.isWeb) {
          updateAppState({
            isAuthenticated: true,
            userRole: 'administracion',
            currentView: 'dashboard',
            currentUser: {
              nombre: 'Usuario Local',
              email: 'local@vidawasi.com',
              rol: 'administracion'
            }
          });
        }
      } finally {
        updateAppState({ appInitialized: true });
      }
    };

    initializeApp();
  }, []);

  // Helper para actualizar estado
  const updateAppState = (updates) => {
    setAppState(prev => ({ ...prev, ...updates }));
  };

  // Manejador de errores centralizado
  const handleAppError = (error) => {
    console.error('Error:', error);
    const message = appState.isWeb 
      ? 'Modo desarrollo con datos locales' 
      : error.message;
    updateAppState({ error: message });
  };

  // Operaciones de autenticación
  const handleAuth = async (action, ...args) => {
    try {
      updateAppState({ error: null });
      const result = await {
        login: loginUser,
        register: registerUser,
        logout: logoutUser
      }[action](...args);

      if (action === 'login' && result) {
        updateAppState({
          isAuthenticated: true,
          userRole: result.rol,
          currentUser: result,
          currentView: 'dashboard'
        });
      }

      return result;
    } catch (error) {
      handleAppError(error);
      throw error;
    }
  };

  // Renderizado condicional
  if (!appState.appInitialized) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
          <p className="mt-2">Inicializando aplicación local...</p>
          {appState.isWeb && (
            <p className="text-sm text-gray-500 mt-2">Usando modo simulado</p>
          )}
        </div>
      </div>
    );
  }

  if (appState.error && !appState.isWeb) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center p-6 bg-white rounded-lg shadow-md max-w-md mx-4">
          <h2 className="text-xl font-bold text-red-600 mb-2">Error</h2>
          <p className="mb-4 text-gray-700">{appState.error}</p>
          <button
            onClick={() => window.location.reload()}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
          >
            Recargar
          </button>
        </div>
      </div>
    );
  }

  // Componente principal
  return (
    <div className="min-h-screen bg-gray-100">
      {!appState.isAuthenticated ? (
        <div className="flex items-center justify-center min-h-screen p-4">
          {appState.showRegister ? (
            <AuthRegisterForm 
              onRegister={(data) => handleAuth('register', data)} 
              onSwitchToLogin={() => updateAppState({ showRegister: false })} 
              error={appState.error}
            />
          ) : (
            <AuthLoginForm 
              onLogin={(email, pass) => handleAuth('login', email, pass)} 
              onSwitchToRegister={() => updateAppState({ showRegister: true })} 
              error={appState.error}
            />
          )}
        </div>
      ) : (
        <div className="flex">
          <NavigationSidebar 
            currentView={appState.currentView}
            setCurrentView={(view) => updateAppState({ currentView: view })}
            userRole={appState.userRole}
            onLogout={() => handleAuth('logout')}
          />

          <main className="flex-1 p-4 md:p-6">
            <div className="bg-white rounded-lg shadow p-4 md:p-6">
              <div className="mb-4 flex flex-col md:flex-row md:justify-between md:items-center gap-2">
                <h2 className="text-xl font-semibold">
                  {{
                    dashboard: 'Dashboard',
                    patients: 'Pacientes',
                    records: 'Registros',
                    appointments: 'Citas',
                    education: 'Material Educativo',
                    users: 'Usuarios'
                  }[appState.currentView]}
                </h2>
                <div className="flex items-center gap-2">
                  <span className="text-sm text-gray-500">
                    {appState.currentUser?.nombre} ({appState.currentUser?.rol})
                  </span>
                  {appState.isWeb && (
                    <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">
                      Modo local
                    </span>
                  )}
                </div>
              </div>
              <div className="mt-4">
                {renderView(appState.currentView, appState.userRole)}
              </div>
            </div>
          </main>
        </div>
      )}
    </div>
  );

  // Función para renderizar vistas
  function renderView(view, role) {
    const views = {
      patients: <PatientList mode={role === 'cuidador' ? 'view' : 'edit'} />,
      records: <PatientRecordForm />,
      appointments: <AppointmentManagement userRole={role} />,
      education: <EducationalMaterials />,
      users: role === 'administracion' 
        ? <UserManagement /> 
        : <div className="p-4 text-red-500">Acceso no autorizado</div>,
      dashboard: <DashboardStats userRole={role} />
    };
    return views[view] || views.dashboard;
  }
};

export default App;
