import React, { useState, useEffect } from 'react';
import { 
  initDB, 
  loginUser, 
  registerUser, 
  getCurrentUser, 
  logoutUser,
  resetDB // Solo para desarrollo
} from './utils/database';
import AuthLoginForm from './components/AuthLoginForm';
import AuthRegisterForm from './components/AuthRegisterForm';
import PatientList from './components/PatientList';
import PatientRecordForm from './components/PatientRecordForm';
import AppointmentManagement from './components/AppointmentManagement';
import EducationalMaterials from './components/EducationalMaterials';
import NavigationSidebar from './components/NavigationSidebar';
import DashboardStats from './components/DashboardStats';
import UserManagement from './components/UserManagement';

const App = () => {
  const [currentView, setCurrentView] = useState('login');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [appInitialized, setAppInitialized] = useState(false);
  const [error, setError] = useState(null);

  // Inicialización de la aplicación
  useEffect(() => {
    const initializeApp = async () => {
      try {
        console.log('[App] Inicializando aplicación...');

        // Para desarrollo: Descomentar si necesitas resetear la DB
        // await resetDB();

        await initDB();
        console.log('[App] Base de datos inicializada');

        const user = await getCurrentUser();
        console.log('[App] Usuario actual:', user);

        if (user) {
          setIsAuthenticated(true);
          setUserRole(user.rol);
          setCurrentUser(user);
          setCurrentView('dashboard');
        }
      } catch (err) {
        console.error('[App] Error en inicialización:', err);
        setError(formatError(err));
      } finally {
        setAppInitialized(true);
      }
    };

    initializeApp();
  }, []);

  const formatError = (error) => {
    if (error.message.includes('no such table')) {
      return 'Error en la estructura de la base de datos. Recarga la aplicación.';
    }
    return error.message || 'Error al conectar con la base de datos. Recarga la página.';
  };

  const handleLogin = async (email, password) => {
    try {
      setError(null);
      const user = await loginUser(email, password);

      if (user) {
        setIsAuthenticated(true);
        setUserRole(user.rol);
        setCurrentUser(user);
        setCurrentView('dashboard');
        return true;
      }
      throw new Error('Credenciales incorrectas');
    } catch (err) {
      console.error('[App] Error en login:', err);
      setError(formatError(err));
      throw err;
    }
  };

  const handleRegister = async (userData) => {
    try {
      setError(null);
      await registerUser(userData);
      alert('¡Registro exitoso! Por favor inicia sesión');
      setShowRegister(false);
      return true;
    } catch (err) {
      console.error('[App] Error en registro:', err);
      setError(formatError(err));
      throw err;
    }
  };

  const handleLogout = async () => {
    try {
      await logoutUser();
      setIsAuthenticated(false);
      setUserRole(null);
      setCurrentUser(null);
      setCurrentView('login');
      setShowRegister(false);
      setError(null);
    } catch (err) {
      console.error('[App] Error al cerrar sesión:', err);
      setError(formatError(err));
    }
  };

  const renderView = () => {
    switch(currentView) {
      case 'patients':
        return <PatientList mode={userRole === 'cuidador' ? 'view' : 'edit'} />;
      case 'records':
        return <PatientRecordForm />;
      case 'appointments':
        return <AppointmentManagement userRole={userRole} />;
      case 'education':
        return <EducationalMaterials />;
      case 'users':
        return userRole === 'administracion' ? <UserManagement /> : 
          <div className="p-4 text-red-500">Acceso no autorizado</div>;
      case 'dashboard':
      default:
        return <DashboardStats userRole={userRole} />;
    }
  };

  if (!appInitialized) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
          <p className="mt-2">Cargando aplicación...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center p-6 bg-white rounded-lg shadow-md max-w-md mx-4">
          <h2 className="text-xl font-bold text-red-600 mb-2">Error</h2>
          <p className="mb-4 text-gray-700">{error}</p>
          <div className="flex justify-center gap-4">
            <button
              onClick={() => window.location.reload()}
              className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
            >
              Recargar
            </button>
            {process.env.NODE_ENV === 'development' && (
              <button
                onClick={async () => {
                  try {
                    await resetDB();
                    window.location.reload();
                  } catch (err) {
                    setError('No se pudo resetear la DB');
                  }
                }}
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition"
              >
                Resetear DB (Dev)
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {!isAuthenticated ? (
        <div className="flex items-center justify-center min-h-screen p-4">
          {showRegister ? (
            <AuthRegisterForm 
              onRegister={handleRegister} 
              onSwitchToLogin={() => setShowRegister(false)} 
              error={error}
            />
          ) : (
            <AuthLoginForm 
              onLogin={handleLogin} 
              onSwitchToRegister={() => setShowRegister(true)} 
              error={error}
            />
          )}
        </div>
      ) : (
        <div className="flex">
          <NavigationSidebar 
            currentView={currentView}
            setCurrentView={setCurrentView}
            userRole={userRole}
            onLogout={handleLogout}
          />

          <main className="flex-1 p-4 md:p-6">
            <div className="bg-white rounded-lg shadow p-4 md:p-6">
              <div className="mb-4 flex flex-col md:flex-row md:justify-between md:items-center gap-2">
                <h2 className="text-xl font-semibold">
                  {{
                    dashboard: 'Dashboard',
                    patients: 'Pacientes',
                    records: 'Registros',
                    appointments: 'Citas',
                    education: 'Material Educativo',
                    users: 'Usuarios'
                  }[currentView]}
                </h2>
                <span className="text-sm text-gray-500">
                  {currentUser?.nombre} ({currentUser?.rol})
                </span>
              </div>
              <div className="mt-4">
                {renderView()}
              </div>
            </div>
          </main>
        </div>
      )}
    </div>
  );
};

export default App;
